App({
  globalData: {
    shareTitle:'',
    shareImgUrl:'',
    invCode:'',
    invCoded:'',
    baiduMapKey:"ktX0SczTKEP6Yu6BkNNPzluGQXOhbQNT",
    userId: "",
    isLogin: false,
    token: "",
    userInfo: null,
    //baseUrl: "https://xny.tongyunzn.com",
    baseUrl: "https://testxny.tongyunzn.com",
    imgUrl: "https://img.tongyunzn.com/tyapplet/",
    staticImg:'https://img.tongyunzn.com/tyxcxtp/', //静态图片地址
    serviceUrl: "https://testxny.tongyunzn.com/cgi", //客服地址
  },
  onLaunch: function () {
    var that = this
    if (wx.canIUse('getUpdateManager')) {
      const updateManager = wx.getUpdateManager()
      updateManager.onCheckForUpdate(function (res) {
        if (res.hasUpdate) {
          updateManager.onUpdateReady(function () {
            wx.showModal({
              title: '更新提示',
              content: '新版本已经准备好，是否重启应用？',
              success: function (res) {
                if (res.confirm) {
                  updateManager.applyUpdate()
                }
              }
            })
          })
          updateManager.onUpdateFailed(function () {
            wx.showModal({
              title: '已经有新版本了哟~',
              content: '新版本已经上线啦~，请您删除当前小程序，重新搜索打开哟~'
            })
          })
        }
      })
    }

  },

});
