// components/address/address.js
Component({
    /**
     * 组件的属性列表
     */
    properties: {
        addresslist: Array,
        defaultAddress: Object
    },

    /**
     * 组件的初始数据
     */
    data: {
        addresslist:[],
        defaultAddress:{}
    },

    /**
     * 组件的方法列表
     */
    methods: {
        handleAddress: function(e){
            var provinceVo = e.currentTarget.dataset.address.provinceVo.name
            var cityVo = e.currentTarget.dataset.address.cityVo.name
            var countyVo = e.currentTarget.dataset.address.countyVo.name
            var detailed = e.currentTarget.dataset.address.detailed
            var address = {...e.currentTarget.dataset.address, detailed: provinceVo + cityVo + countyVo +detailed}
            this.triggerEvent('detailAddress', {address})
        },
        addAddress:function(){
            wx.navigateTo({
              url: '/pages/addressedit/addressedit?type=add',
            })
        }
    }
})
