const util = require("../../utils/util");
var bmap = require('../../utils/bmap-wx.min');
var wxMarkerData = [];
const app = getApp();
Page({

  /**
   * 页面的初始数据
   */
  data: {
    showClear:false,
    content:'',
    sign:'',
    isGetAddressAuto:false,
  },
  //获取当前位置
  getLocation:function(e){//获取位置
    if(this.data.sign !='address'){
      return false
    }
    this.setData({
      isGetAddressAuto:false
    })
    wx.setStorage({
      key: "isAutoAddress",
      data: 2,
    });
  //  console.log('点击获取事件参数',e.currentTarget.dataset.id)
    var _that = this;
    wx.authorize({
      scope: 'scope.userLocation',
      success (res) {
      //  console.log('authorize__________',res)
        //--------------------baidu map start
        var BMap = new bmap.BMapWX({
          ak: app.globalData.baiduMapKey
        });
        var fail = function(data) {
          //  console.log(data)
        };
        var success = function(data) {
            wxMarkerData = data.wxMarkerData;
            _that.setData({
              content:wxMarkerData[0].address
            })
        }
        BMap.regeocoding({
            fail: fail,
            success: success,
            // iconPath: '../../img/marker_red.png',
            // iconTapPath: '../../img/marker_red.png'
        })
        //----------------------------baidu map end
      }
    })
  },
  handleInputChange:function(e){
    let currentValue = e.detail.value;
    if(currentValue){
      this.setData({
        content:currentValue,
        showClear:true
      })
    }else{
      this.setData({
        showClear:false
      })
    }
  //  console.log(currentValue)
  },
  clearContent:function(){
    this.setData({
      content:''
    })
  },
  handleCancel:function(){
    wx.navigateBack({
      delta:1,
    })
  },
  handleSaved:function(){
    let that =this;

    if(that.data.content ==''){
      wx.showToast({
        title: '不得为空',
        icon: "none",
        duration: 2000,
      });
      return false
    }
    if(that.data.sign =='phone' && this.data.content.length != 11 ){
      wx.showToast({
        title: "手机号位数部不正确",
        icon: "none",
        duration: 2000,
      });
      return false
    }
    if (that.data.sign =='phone' && util.phone(this.data.content)) {
      wx.showToast({
        title: "手机号格式不正确",
        icon: "none",
        duration: 2000,
      });
      return false;
    }
    let sign = that.data.sign
    let param={
      id:app.globalData.userId,
      [that.data.sign]:that.data.content
    }
  //  console.log('sign--------',sign)
    wx.request({
      url: app.globalData.baseUrl + "/ty/heatingApplet/user/perfectUser",
      method: "POST",
      header: {
        "content-type": "application/json", // 默认值
        token: app.globalData.token,
      },
      data: param,
      success: (res) => {
      //  console.log('res___',res.data.status)
        if(res.data.status !='0000'){
          wx.showToast({
            title: res.data.message,
            icon: "none",
            duration: 2000,
          });
        }else{
          wx.showModal({
            title: '提示',
            content: res.data.message,
            showCancel:false,
            mask:true,
            success (res) {
              wx.navigateBack({
                delta:1,
              })
            }
          })
        }
      },
    });
  },
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    //console.log('参数值',options.sign)
    var _that =this;
    if(options.sign == 'address'){

      this.setData({
        isGetAddressAuto:true
      })
      wx.getStorage({
        key: "isAutoAddress",
        success(res) {

         if( res.data==2){
          _that.setData({
            isGetAddressAuto:false
          })
         }
         // console.log('双击6666.老铁——————————————————————————————————',res)

        }
      });
    }
    if(options.val != 'undefined'){
      this.setData({
        showClear:true,
        content:options.val
      })
    }
    this.setData({
      sign:options.sign,

    })

  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  }
})
