// pages/address/address.js
var app = getApp()
Page({

    /**
     * 页面的初始数据
     */
    data: {
        nodata: true,
        addresslist: []
    },

    addAddress:function(){
        wx.navigateTo({
          url: '/pages/addressedit/addressedit?type=add',
        })
    },

    onEditTap:function(e){
        var params = e.currentTarget.dataset.params
        wx.navigateTo({
            url: '/pages/addressedit/addressedit?type=edit',
            success: function(res) {
                // 通过eventChannel向被打开页面传送数据
                res.eventChannel.emit('acceptDataFromOpenerPage', { ...params })
              }
        })
    },

    getAddressList:function(){
        var _this = this
        wx.request({
          url:  app.globalData.baseUrl + `/ty/heatingApplet/address/myAddressList?pageNo=1&pageSize=30&userId=${app.globalData.userId}`,
          method: "get",
          header: {
              "content-type": "application/json", // 默认值
              "token": app.globalData.token
          },
          data: {},
          success:function(res){
              wx.hideLoading();
              if(res.data.status === "0000"){
                  if(res.data.result.list.length == 0){
                    _this.setData({
                        nodata: true
                    })
                  }else{
                    _this.setData({
                        nodata: false,
                        addresslist: res.data.result.list
                    })
                  }
                   
              }else{
                _this.setData({
                    nodata: true
                })
              }
          }
        })
    },


    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        wx.showLoading({
            title: '页面加载中...',
        })

        this.getAddressList()
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {
        this.getAddressList()
    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})