// pages/addressedit/addressedit.js
var app = getApp()
var util = require('../../utils/util')
Page({

    /**
     * 页面的初始数据
     */
    data: {
        type: "", // 新增 add/编辑 edit
        id: "",  //地址id  编辑地址时用
        name: "",
        phone: "",
        area: "",
        regionId: "",
        detailed: "",
        status: "2", //1为默认，2为非默认
        radioChecked: false,
        showAddressModel: false,
        province: [],
        city: [],
        county: [],
        addressView: [],
        areaArray: [], //所选城市数组
        show: false
    },

    onRadioTap: function () {
        this.setData({
            radioChecked: !this.data.radioChecked,
            status: !this.data.radioChecked ? "1" : "2"
        })
    },

    onSaveTap: function () {
        var _this = this;
        if (!this.data.name) {
            wx.showToast({
                title: "姓名不得为空",
                icon: "none",
                duration: 2000,
            });
            return false;
        }
        if (!this.data.phone) {
            wx.showToast({
                title: "手机号不得为空",
                icon: "none",
                duration: 2000,
            });
            return false;
        }
        if (this.data.phone.length != 11) {
            if (util.phone(this.data.userPhone)) {
                wx.showToast({
                    title: "手机号位数不正确",
                    icon: "none",
                    duration: 2000,
                });
            }
            return false;
        }
        if (util.phone(this.data.phone)) {
            wx.showToast({
                title: "手机号格式不正确",
                icon: "none",
                duration: 2000,
            });
            return false;
        }
        if (!this.data.area) {
            wx.showToast({
                title: "请选择所在区域",
                icon: "none",
                duration: 2000,
            });
            return false;
        }
        if (!this.data.detailed) {
            wx.showToast({
                title: "详细地址不得为空",
                icon: "none",
                duration: 2000,
            });
            return false;
        }

        let params = {
            userId: app.globalData.userId,
            name: _this.data.name,
            phone: _this.data.phone,
            regionId: _this.data.regionId,
            detailed: _this.data.detailed,
            status: _this.data.status,
        }

        if (this.data.type === 'add') {
            params = params
        } else if (this.data.type === 'edit') {
            params = { id: this.data.id, ...params }
        }

        wx.request({
            url: app.globalData.baseUrl + "/ty/heatingApplet/address/upAddress",
            method: "post",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: params,
            success(res) {
                if (res.data.status == '0000') {
                    _this.setData({
                        show: true
                    })
                } else {
                    wx.showToast({
                        title: res.data.message,
                        icon: "none",
                        duration: 2000,
                    });
                }
            }
        })
    },

    goUrl(){
        wx.navigateBack({
            delta: 1,
        })
    },

    onDeleteTap: function () {
        var _this = this
        wx.showLoading({
            title: '',
        })
        wx.request({
            url: app.globalData.baseUrl + `/ty/heatingApplet/address/delAddress`,
            method: "post",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {
                id: _this.data.id
            },
            success: function (res) {
                if(res.data.status === '0000'){
                    wx.hideLoading({
                        success: function () {
                            wx.navigateBack({
                                delta: 1,
                            })
                        }
                    })
                }else{
                    wx.showToast({
                        title: res.data.message,
                        icon: "none",
                        duration: 2000,
                    });
                }
            }
        })
    },

    setAreaTap: function (name) {
        var select = "请选择"
        var areaArray = wx.getStorageSync('areaArray') || []
        areaArray.pop()
        name !== '' && areaArray.push(name)
        areaArray.push(select)
        wx.setStorageSync('areaArray', areaArray)
        this.setData({
            areaArray: areaArray
        })
    },

    onRegionTap: function (e) {
        wx.showLoading({
            title: "加载中...",
        })
        var _this = this
        var parentId = e.currentTarget.dataset.id || ''
        var name = e.currentTarget.dataset.name || ''
        var url = parentId ? `/ty/heatingApplet/address/addressList?parentId=${parentId}` : `/ty/heatingApplet/address/addressList`;
        this.setAreaTap(name);
        wx.request({
            url: app.globalData.baseUrl + url,
            method: "get",
            header: {
                "content-type": "application/x-www-form-urlencoded", // 默认值
                "token": app.globalData.token
            },
            success: function (res) {
                if (res.data.status == "0000") {
                    _this.setData({
                        addressView: res.data.result,
                        showAddressModel: true,
                    })

                    if (res.data.result.length == 0) {
                        var area = ''
                        for(var i=0; i < _this.data.areaArray.length-1; i++){
                            area += _this.data.areaArray[i]
                        }
                        _this.setData({
                            showAddressModel: false,
                            regionId: parentId,
                            area: area
                        })
                        wx.removeStorageSync('areaArray')
                    }
                }
            },
            complete: function () {
                wx.hideLoading()
            }
        })
    },
    closedModel: function (e) {
        this.setData({
            showAddressModel: false,
        });
        wx.showTabBar({
            animation: false,
            success: (res) => {
                console.log("showTabBar", res);
            },
        });
        wx.removeStorageSync('areaArray')
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        wx.removeStorageSync('areaArray')
        if (options.type == 'add') {
            wx.setNavigationBarTitle({
                title: '新增地址'
            })
            this.setData({
                type: 'add'
            })
        }
        if (options.type == 'edit') {
            wx.setNavigationBarTitle({
                title: '编辑地址'
            })
            this.setData({
                type: 'edit'
            })
        }

        const eventChannel = this.getOpenerEventChannel()
        eventChannel.on('acceptDataFromOpenerPage', (data) => {
            console.log(data)
            this.setData({
                id: data.id,
                name: data.name,
                phone: data.phone,
                area: data.provinceVo.name + data.cityVo.name + data.countyVo.name,
                regionId: data.county,
                detailed: data.detailed,
                status: data.status, //1为默认，2为非默认
                radioChecked: data.status == 1 ? true : false,
            })
        })
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {
        wx.removeStorageSync('areaArray')
    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {
        wx.removeStorageSync('areaArray')
    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})