// pages/applyagent/applyagent.js
var util = require('../../utils/util')
var app = getApp()
Page({

    /**
     * 页面的初始数据
     */
    data: {
        username: '',
        phone: '',
        code:'',
        proxyArea:'',
        countdowntime: 60,
        timeView: 60,
        isSend: false,
        show: false
    },

    getCode:function(){
        var _this = this;
        if (!this.data.phone) {
            wx.showToast({
              title: "手机号不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (this.data.phone.length != 11) {
            if (util.phone(this.data.userPhone)) {
              wx.showToast({
                title: "手机号位数不正确",
                icon: "none",
                duration: 2000,
              });
            }
            return false;
          }
          if (util.phone(this.data.phone)) {
            wx.showToast({
              title: "手机号格式不正确",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          wx.request({
            url: app.globalData.baseUrl + "/ty/heatingAdmin/sedSms/makeAgentSms",
            method: "post",
            header: {
                "content-type": "application/x-www-form-urlencoded", // 默认值
                "token": app.globalData.token
            },
            data: {
                phone: _this.data.phone,
            },
            success(res){
              _this.countdown(_this.data.countdowntime);
            }
          })
    },

    onSubmitTap:function(){
        var _this = this;
        if (!this.data.username) {
            wx.showToast({
              title: "姓名不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (!this.data.phone) {
            wx.showToast({
              title: "手机号不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (this.data.phone.length != 11) {
            if (util.phone(this.data.phone)) {
              wx.showToast({
                title: "手机号位数不正确",
                icon: "none",
                duration: 2000,
              });
            }
            return false;
          }
          if (util.phone(this.data.phone)) {
            wx.showToast({
              title: "手机号格式不正确",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (!this.data.code) {
            wx.showToast({
              title: "验证码不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (!this.data.proxyArea) {
            wx.showToast({
              title: "代理区域不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }

          wx.request({
            url: app.globalData.baseUrl + "/ty/heatingApplet/agent/addAgent",
            method: "post",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {
                username: _this.data.username,
                phone: _this.data.phone,
                code: _this.data.code,
                proxyArea: _this.data.proxyArea	
            },
            success(res){
                if (res.data.status == "0000") {
                  _this.setData({
                    show: true
                })
                }else{
                    wx.showToast({
                        title: res.data.message,
                        icon: "none",
                        duration: 2000,
                      });
                }
            }
        })
    },

    goUrl(){
      wx.navigateBack({
          delta: 1,
      })
  },

    countdown(time){
        this.setData({
          timeView : time,
          isSend: true
        })
        let timer = setInterval(() => {
            if(time == 0){
                clearInterval(timer)
                this.setData({
                  isSend: false
                })
            }else {
                this.setData({
                  timeView : --time
                })
            }
        },1000)
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {

    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})