// pages/bankinfoedit/bankinfoedit.js
var app = getApp();
const util = require("../../utils/util");
Page({

    /**
     * 页面的初始数据
     */
    data: {
        bankCardNo: '',
        userName: '',
        reservedPhone: '',
        bankName: ''
    },

    getUserInfo: function () {
        var that = this;
        wx.request({
            url: app.globalData.baseUrl + "/ty/heatingApplet/user/userinfo",
            method: "POST",
            header: {
                "content-type": "application/json", // 默认值
                token: app.globalData.token,
            },
            data: {
                id: app.globalData.userId
            },
            success: (res) => {
                that.setData({
                    userName: res.data.result.bankUserName,
                    bankCardNo: res.data.result.bankCardNo,
                    bankName: res.data.result.bankName,
                    reservedPhone: res.data.result.reservedPhone
                });
            },
        });
    },
    getCardInfo: function (e) {
        var that = this
        wx.request({
            url: app.globalData.baseUrl + "/ty/heatingApplet/bank/carkByBin",
            method: "post",
            header: {
                "content-type": "application/json", // 默认值
                token: app.globalData.token,
            },
            data: {
                cardNu: e.detail.value
            },
            success: (res) => {
                if (res, res.data.result) {
                    that.setData({
                        bankName: res.data.result,
                    });
                }else if(res.data.status == '7010'){
                    wx.showToast({
                      title: res.data.message,
                      icon: 'none',
                      duration: 2000
                    })
                }

            },
        });
    },

    onSaveTap: function(){
        var _this = this
        wx.showLoading({
          title: 'none',
        })
        if (!this.data.userName) {
            wx.showToast({
              title: "姓名不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (util.chinese(this.data.userName)) {
            wx.showToast({
              title: "姓名必须为中文",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (!this.data.bankCardNo) {
            wx.showToast({
              title: "卡号不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }
          if (!this.data.bankName) {
            wx.showToast({
              title: "卡名称不得为空",
              icon: "none",
              duration: 2000,
            });
            return false;
          }

          wx.request({
            url: app.globalData.baseUrl+ `/ty/heatingApplet/user/updateBankInfo`,
            method: "POST",
            header: {
                "content-type": "application/json", // 默认值
                token: app.globalData.token,
            },
            data: {
                userId: app.globalData.userId,
                bankCardNo: _this.data.bankCardNo,
                userName: _this.data.userName,
                reservedPhone: _this.data.reservedPhone,
                bankName: _this.data.bankName,
            },
            success: (res) => {
                wx.hideLoading({
                  success: (res) => {
                    wx.navigateBack({
                        delta: 1
                      })
                  },
                })
            }
          })
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        this.getUserInfo()
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})