// pages/casedetail/casedetail.js
var app = getApp()
Page({

    /**
     * 页面的初始数据
     */
    data: {
        casedetail: {}
    },

    getcasedetail: function(id){
        var _this = this
        wx.request({
          url: app.globalData.baseUrl + `/ty/heatingApplet/cases/queryById?id=${id}`,
          method: "get",
          header: {
              "content-type": "application/json", // 默认值
              "token": app.globalData.token
          },
          success: (res) => {
              wx.hideLoading()
            if(res.data.status === "0000"){
                _this.setData({
                    casedetail: res.data.result
                })
            }else{
                wx.showToast({
                  title: res.data.message,
                  icon:'none',
                  duration:2000
                })
            }
          }
        })
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
            wx.showLoading({
              title: '加载中...',
            })
          this.getcasedetail(options.id)
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})