// pages/feedback/feedback.js
const app = getApp();
Page({

    /**
     * 页面的初始数据
     */
    data: {
        content:'',
        userId: '',
        show:''
    },

    onSubmitTap: function(){
        var _this = this
        if(!this.data.content){
            wx.showToast({
                title: "内容不得为空",
                icon: "none",
                duration: 2000,
              });
              return false;
        }
        wx.request({
            url: app.globalData.baseUrl + "/ty/heatingApplet/feedback/addFeedback",
            method: "post",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {
                userId: app.globalData.userId,
                content: _this.data.content,
            },
            success(res){
                if (res.data.status == "0000") {
                    _this.setData({
                        show: true
                    })
                }else{
                    wx.showToast({
                        title: res.data.message,
                        icon: "none",
                        duration: 2000,
                      });
                }
               
            }
        })
    },

    goUrl(){
        wx.navigateBack({
            delta: 1,
        })
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {

    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})