// pages/form/comb.js
Page({

  /**
   * 页面的初始数据
   */
  data: {
    isbg:false,
    showe: false, //企业主体证明类型
    isshowe:'请选择',	
    items: [{
            message: '营业执照',
          },{
            message: '组织机构代码证'
          },{
            message: '宗教活动场所登记证'
          },{
            message: '社会团体法人登记证书'
          },{
            message: '军队、武警出具的办理用电业务的证明'
          }],
	showa: false, //企业主体证明类型
	isshowa:'请选择',	
	itemsa: [{
	        message: '产权证',
	      },{
	        message: '国有土地使用证'
	      },{
	        message: '集体土地使用证'
	      },{
	        message: '购房合同'
	      },{
	        message: '法律文书'
	      },{
	        message: '产权合法证明'
	      },{
	        message: '其他(产权证、租赁协议)'
	      }],	  
  },
//企业主体证明类型选择
onBtnb:function (){	
	this.setData({showe:true,isbg:true})
},
selBtnb:function (res){
	this.setData({isshowe:this.data.items[res.currentTarget.id].message})
	this.setData({showe:false,isbg:false})		
},
cloBtnb:function (){
	this.setData({showe:false,isbg:false})
},
//产权证明类型选择
onBtna:function (){	
	this.setData({showa:true,isbg:true})
},
selBtna:function (res){
	this.setData({isshowa:this.data.itemsa[res.currentTarget.id].message})
	this.setData({showa:false,isbg:false})		
},
cloBtna:function (){
	this.setData({showa:false,isbg:false})
},
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {

  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  }
})