var app = getApp()
var util = require('../../utils/util')
Page({

  /**
   * 页面的初始数据
   */
  data: {
    showModal: false, //取消框
	showd: false, //是否是住宅用电
	isshowd:'请选择',
	itemsshowd:  [{
	      message: '身份证',
	    },{
	      message: '军人证',
	    },{
	      message: '护照',
	    },{
	      message: '户口薄',
	    },{
	      message: '台胞证及港澳身份证件',
	    },{
	      message: '公安机关户籍证明',
	    }],
	isbg:false,		
  },

  formModal:function (){
	 var _this = this;
	 if (!this.data.ownerName) {
	     wx.showToast({
	         title: "姓名不得为空",
	         icon: "none",
	         duration: 2000,
	     });
	     return false;
	 }
	 if (!this.data.applicantPhone) {
	     wx.showToast({
	         title: "手机号不得为空",
	         icon: "none",
	         duration: 2000,
	     });
	     return false;
	 }
	 if (this.data.applicantPhone.length != 11) {
	     if (util.phone(this.data.applicantPhone)) {
	         wx.showToast({
	             title: "手机号位数不正确",
	             icon: "none",
	             duration: 2000,
	         });
	     }
	     return false;
	 }
	 if (util.phone(this.data.applicantPhone)) {
	     wx.showToast({
	         title: "手机号格式不正确",
	         icon: "none",
	         duration: 2000,
	     });
	     return false;
	 }
  },
  onBtna:function (){
  	this.setData({showd:true,isbg:true})
  },
  selBtnb:function (res){
  	this.setData({isshowd:this.data.itemsshowd[res.currentTarget.id].message})
  	this.setData({showd:false,isbg:false})		
  },
  cloBtnb:function (){
  	this.setData({showd:false,isbg:false})
  },
//取消框
oshowModal:function (){
	this.setData({showModal:true})
},
cshowModal:function (){
	this.setData({showModal:false})
	wx.redirectTo({url:"./pera"})
},
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {

  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

})