const app = getApp();
Page({

  /**
   * 页面的初始数据
   */
  data: {
     isBg:false,
	 isInfo:false,
  },

  /**
   * 生命周期函数--监听页面加载
   */
    onLoad: function (options) {  
		
        //this.getIsUserInfo();
    },
	getIsUserInfo:function(){
	    var that=this;	
	    if(wx.getStorageSync('nick')=="") 
	    {
			that.setData({
				isBg:true,
				isInfo:true
			})
		}
	},
	bindGetUserInfo: function (res) {
		var userInfo = res.detail.userInfo;
		wx.setStorageSync('nick',userInfo.nickName)
		this.setData({
			isBg:false,
			isInfo:false
		})
	},
	onReady: function () {

	},

    onShow: function () {

    },
   
})