// pages/form/islogin.js
Page({

  /**
   * 页面的初始数据
   */
  data: {
     lm:'',
	 tid:0
  },

  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
	  
	  this.setData({
		  tid:options.tid,
		  lm:options.lm
	  })	  
  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },


  bindGetUserInfo: function (res) {
  		var userInfo = res.detail.userInfo;
		wx.setStorage({
		  data: userInfo.nickName || '',
		  key: 'userName',
		})
		wx.setStorage({
		  key: "userIcon",
		  data: userInfo.avatarUrl,
		});
		that.setData({
		  userName: userInfo.nickName,
		  userIcon: userInfo.avatarUrl
		});
		
		if(this.data.lm=="cart")
		{
			wx.switchTab({
				url:"/pages/cart/cart"
			})
			
		}
		else if(this.data.lm=="inviteu"){
			wx.switchTab({
				url:"/pages/inviteuser/inviteuser"
			})
		}
		else if(this.data.lm=="applyi"){
			wx.redirectTo({
				url:"/pages/applyinstall/applyinstall"
			})
		}
		else if(this.data.lm=="addr"){
			wx.redirectTo({
				url:"/pages/addressedit/addressedit"
			})
		}
		else if(this.data.lm=="pinfo"){
			wx.redirectTo({
				url:"/pages/productdetail/productdetail?id="+this.data.tid
			})
		}
		else
		{
			wx.switchTab({
				url:"/pages/index/index"
			})
		}
		
  		// wx.setStorageSync('nick',userInfo.nickName)
  		// this.setData({
  		// 	isBg:false,
  		// 	isInfo:false
  		// })
	
  },
})