const app = getApp();
Page({

  /**
   * 页面的初始数据
   */
  data: {
    invCode:'',
    invWayImg:{
      img1:'https://img.tongyunzn.com/xc_bz1.png',
      img2:'https://img.tongyunzn.com/xc_bz2.png'
    },
    way:"邀请方式",
    rule:"返利规则",
    othes:"其他说明",
    invited:"已申请办理",
    isinvited:true,
    rebateExplanation:'',
    otherExplanation:''


  },
  getInfo:function(){
    var that =this;
    wx.request({
      url: app.globalData.baseUrl +"/ty/heatingApplet/keyValue/getKeyValue",
      data: {},
      header: {
        "content-type": "application/json", // 默认值
        token: app.globalData.token,
      },
      method: "post",
      success: function (res) {
        if(res.data.status !='0000'){
          wx.showToast({
            icon:"none",
            title: res.data.message,
          })
          return false
        }
        that.setData({
          rebateExplanation:res.data.result.rebateExplanation,
          otherExplanation:res.data.result.otherExplanation
          
        })

      },
    });
  },
  copyInvCode:function(){
    let that =this
    wx.setClipboardData({
      data: that.data.invCode,
      success (res) {
        wx.showToast({
          title: '邀请码已复制',
          icon: 'success',
          duration: 2000
        })
        // wx.getClipboardData({
        //   success (res) {
        //     console.log(res.data) // data
        //   }
        // })
      }
    })
  },
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    this.getInfo()
    this.setData({
      invCode:app.globalData.invCode
    })
  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function (res) {
    if (res.from === 'button') {
      // 来自页面内转发按钮  
    }
    return {
      title: app.globalData.shareTitle,
      imageUrl:app.globalData.shareImgUrl,
      path: '/pages/index/index?invCode='+app.globalData.invCode
    }
  }
})
