// pages/order/order.js
var app = getApp()
Page({

    /**
     * 页面的初始数据
     */
    data: {
        orderDetail: {},
        imgBaseUrl: app.globalData.imgUrl,
        orderId: '',
        serviceUrl: app.globalData.serviceUrl
    },

    getOrderDetail: function (orderId) {
        wx.showLoading({
            title: '加载中...',
        })
        var _this = this
        wx.request({
            url: app.globalData.baseUrl + `/ty/heatingApplet/order/orderDetail?orderId=${orderId}`,
            method: "get",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {},
            success: function (res) {
                wx.hideLoading();
                if (res.data.status === "0000") {
                    _this.setData({
                        orderDetail: res.data.result,
                        orderId: res.data.result.orderId
                    })
                    if (res.data.result.status == '2') {
                        wx.setNavigationBarTitle({
                            title: '等待支付'
                        })
                    } else if (res.data.result.status == '4') {
                        wx.setNavigationBarTitle({
                            title: '完成'
                        })
                    }
                } else {
                    wx.showToast({
                        title: res.data.message,
                        icon: '',
                        duration: 2000
                    })
                }
            }
        })
    },

    onCancelTap: function () {
        wx.showLoading({
            title: '取消中...',
        })
        var _this = this
        wx.request({
            url: app.globalData.baseUrl + `/ty/heatingApplet/order/cancelOrder`,
            method: "post",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {
                orderId: _this.data.orderId
            },
            success: function (res) {
                wx.hideLoading();
                if (res.data.status === "0000") {
                    wx.navigateBack({
                        delta: 1,
                    })
                } else {
                    wx.showToast({
                        title: res.data.message,
                        icon: '',
                        duration: 2000
                    })
                }
            }
        })
    },

    onPayTap: function () {
        var _this = this
        wx.request({
            url: app.globalData.baseUrl + `/ty/heatingApplet/order/payPgain?orderId=${_this.data.orderId}`,
            method: "get",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {},
            success: function (res) {
                wx.hideLoading();
                if (res.data.status === "0000") {
                    var payargs = res.data.result
                    wx.requestPayment({
                        timeStamp: payargs.timeStamp,
                        nonceStr: payargs.nonceStr,
                        package: payargs.package,
                        signType: payargs.signType,
                        paySign: payargs.paySign,
                        success(res) {
                            wx.navigateBack({
                                delta: 1,
                            })
                        },
                    })
                } else {
                    wx.showToast({
                        title: res.data.message,
                        icon: '',
                        duration: 2000
                    })
                }
            }
        })
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        this.getOrderDetail(options.orderId)
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})