// pages/orderlist/orderlist.js
var app = getApp()
Page({

    /**
     * 页面的初始数据
     */
    data: {
        orderList:[],
        nodata: true,
        imgBaseUrl: app.globalData.imgUrl,
        status:{
            '2': '待支付',
            '4': '交易成功'
        }
    },

    getOrderList: function () {
        var _this = this
        wx.request({
            url: app.globalData.baseUrl + `/ty/heatingApplet/order/myOrderList?userId=${app.globalData.userId}`,
            method: "get",
            header: {
                "content-type": "application/json", // 默认值
                "token": app.globalData.token
            },
            data: {},
            success: function (res) {
                wx.hideLoading();
                if (res.data.status === "0000") {
                    if (res.data.result.length == 0) {
                        _this.setData({
                            nodata: true
                        })
                    } else {
                        _this.setData({
                            nodata: false,
                            orderList: res.data.result
                        })
                    }

                } else {
                    _this.setData({
                        nodata: true
                    })
                }
                wx.stopPullDownRefresh()
            }
        })
    },

    onOrderTap: function(e){
        wx.navigateTo({
          url: '/pages/order/order?orderId='+ e.currentTarget.dataset.orderid,
        })
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        wx.showLoading({
            title: '加载中...',
          })
        this.getOrderList()
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {
        this.getOrderList()
    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {
        wx.showLoading({
            title: '页面重新加载中...'
        });
        this.getOrderList()
    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})