// pages/productlist/product.js
var app = getApp()
Page({

    /**
     * 页面的初始数据
     */
    data: {
        productlist: [],
        category:'',
        categoryInfo:[]
    },

    //跳转产品详情页
    detailProduct: function (res) {
         // console.log("产品详情I__________", res.detail.id);
        wx.navigateTo({
            url: "../productdetail/productdetail?id=" + res.detail.id,
        });
    },

    getProductlist: function (e,name) {
        wx.showLoading({
            icon: "none",
            title:' 加载中...'
        })
        var _this = this
        var category = e? e.currentTarget.dataset.category: '';
        var name = name?name:''
        this.setData({
            category: category
        })
        var url = app.globalData.baseUrl + "/ty/heatingApplet/equipment/equipmentCenter"
        if(category){
            url += `?category=${category}`
        }else if(name){
            url += `?name=${name}`
        }
        wx.request({
            url: url,
            method: 'GET',
            header: {
                "content-type": "application/json", // 默认值
                token: app.globalData.token,
            },
            success: function(res){
                if (res.data.status) {
                    _this.setData({
                        productlist: res.data.result.list
                    })
                } else {
                    wx.showToast({
                        title: res.data.message,
                        icon: '',
                        duration: 2000
                    })
                }
            },
            complete: function () {
                wx.hideLoading({})
            }
        })
    },

    getProductCategory: function(){
        var _this = this
        wx.request({
            url: app.globalData.baseUrl + "/ty/heatingApplet/equipment/categoryInfo",
            method: 'GET',
            header: {
                "content-type": "application/json", // 默认值
                token: app.globalData.token,
            },
            success: function(res){
                _this.setData({
                    categoryInfo: res.data.result
                })
            }
        })
    },

    onSearchChange: function(e){
        wx.navigateTo({
          url: '/pages/search/search?value='+ e.detail.value,
        })
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
       this.getProductCategory()
        this.getProductlist()
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {
      
    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {
       
    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})