// pages/search/search.js
var app = getApp()
Page({
    /**
     * 页面的初始数据
     */
    data: {
        value:'',
        productlist:[],
        orderNum: '3'
    },

    getProductlist: function (name,orderNum=3) {
        wx.showLoading({
            icon: "none",
            title:' 加载中...'
        })
        var _this = this
        var url = app.globalData.baseUrl + "/ty/heatingApplet/equipment/equipmentCenter?name="+name
        if(orderNum){
            url += `&orderNum=${orderNum}`
        }
        wx.request({
            url: url,
            method: 'GET',
            header: {
                "content-type": "application/json", // 默认值
                token: app.globalData.token,
            },
            success: function(res){
                if (res.data.status) {
                    _this.setData({
                        productlist: res.data.result.list
                    })
                } else {
                    wx.showToast({
                        title: res.data.message,
                        icon: '',
                        duration: 2000
                    })
                }
            },
            complete: function () {
                wx.hideLoading({})
            }
        })
    },

    onSearchChange: function(e){
        this.setData({
            value: e.detail.value
        })
        this.getProductlist(this.data.value, this.data.orderNum)
    },

      //跳转产品详情页
    detailProduct: function (res) {
         // console.log("产品详情I__________", res.detail.id);
        wx.navigateTo({
            url: "../productdetail/productdetail?id=" + res.detail.id,
        });
    },

    onTabTap: function(e){
        this.setData({
            orderNum: e.currentTarget.dataset.ordernum
        })
        this.getProductlist(this.data.value, e.currentTarget.dataset.ordernum)
    },

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        this.setData({
            value: options.value
        })

        this.getProductlist(options.value)
    },

    /**
     * 生命周期函数--监听页面初次渲染完成
     */
    onReady: function () {

    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function () {

    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})